import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Box, Button } from "@material-ui/core";
import { red, grey } from "@material-ui/core/colors";
const useStyles = makeStyles((theme) => ({
  centre: {
    paddingLeft: "75px",
    paddingRight: "45px",
  },
  buttonStyle: {
    background: "transparent",
    minWidth: "20px",
    fontSize: "13px",
    fontWeight: "300",
    color: grey["500"],
    margin: "1px",
    textTransform: "none",
    "&:hover": {
      background: "transparent",
      borderColor: red["200"],
    },
    "&:focus": {
      background: "transparent",
      borderColor: red["700"],
    },
  },
}));
export default function FilterButtons(props) {
  const classes = useStyles();
  // render() {
  return (
    <Box className={classes.centre}>
      <Button
        size="small"
        variant="outlined"
        className={classes.buttonStyle}
        onClick={() => props.switchFilter("All")}
      >
        All
      </Button>
      <Button
        size="small"
        variant="outlined"
        className={classes.buttonStyle}
        onClick={() => props.switchFilter("Active")}
      >
        Active
      </Button>
      <Button
        size="small"
        variant="outlined"
        className={classes.buttonStyle}
        onClick={() => props.switchFilter("Completed")}
      >
        Completed
      </Button>
      <Button
        size="small"
        variant="outlined"
        className={classes.buttonStyle}
        onClick={() => props.switchFilter("Archived")}
      >
        Archived
      </Button>
    </Box>
  );
}
// }
