import React from "react";
import FilterButton from "./Components/FilterButton";
import ActiveTodosCount from "./Components/ActiveTodosCount";
import ClearCompleted from "./Components/ClearCompleted";
import { Box, Button, ButtonGroup, Container } from "@material-ui/core";
import grey from "@material-ui/core/colors/grey";

const theme = {
  spacing: 16,
};
export default class Footer extends React.Component {
  render() {
    return (
      <li id="buttonsLI">
        <ActiveTodosCount activeCount={this.props.todosCount.activeCount} />
        <Box pl={7} pr={2}>
          <ButtonGroup size="small" aria-label="small outlined button group">
            <Button onClick={(e) => this.props.switchFilter("All")}>All</Button>
            <Button onClick={(e) => this.props.switchFilter("Active")}>
              Active
            </Button>
            <Button onClick={(e) => this.props.switchFilter("Completed")}>
              Completed
            </Button>
            <Button onClick={(e) => this.props.switchFilter("Archived")}>
              Archived
            </Button>
          </ButtonGroup>
        </Box>
        {this.props.todosCount.completedCount > 0 && (
          <Button
            size="small"
            style={{ fontSize: "12px", marginLeft: "8px" }}
            onClick={() => this.props.clearCompleted()}
          >
            Clear Completed
          </Button>
        )}
        {/* <FilterButton
            id="All"
            textContent="All"
            switchFilter={this.props.switchFilter}
          />
          <FilterButton
            id="Active"
            textContent="Active"
            switchFilter={this.props.switchFilter}
          />
          <FilterButton
            id="Completed"
            textContent="Completed"
            switchFilter={this.props.switchFilter}
          />
          <FilterButton
            id="Archived"
            textContent="Archived"
            switchFilter={this.props.switchFilter}
          /> */}
        {/* {this.props.todosCount.completedCount > 0 && (
          <ClearCompleted
            id="ClearCompleted"
            textContent="Clear completed"
            clearCompleted={this.props.clearCompleted}
          />
        )} */}
      </li>
    );
  }
}
