import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Box, Button, ButtonGroup } from "@material-ui/core";
const theme = {
  spacing: 16,
};
const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
      width: "60ch",
    },
  },
}));
export default class FilterButtons extends React.Component {
  render() {
    return (
      <Box pl={7} pr={2}>
        <ButtonGroup size="small" aria-label="small outlined button group">
          <Button onClick={() => this.props.switchFilter("All")}>All</Button>
          <Button onClick={() => this.props.switchFilter("Active")}>
            Active
          </Button>
          <Button onClick={() => this.props.switchFilter("Completed")}>
            Completed
          </Button>
          <Button onClick={() => this.props.switchFilter("Archived")}>
            Archived
          </Button>
        </ButtonGroup>
      </Box>
    );
  }
}
