import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Box, Button, ButtonGroup } from "@material-ui/core";
import { red } from "@material-ui/core/colors";
const theme = {
  spacing: 16,
};
const useStyles = makeStyles((theme) => ({
  hover: {
    border: "none",
    "&:hover": {
      borderColor: red["200"],
    },
    "&:focus": {
      borderColor: red["600"],
    },
  },
}));
export default function FilterButtons(props) {
  const classes = useStyles();
  // render() {
  return (
    <Box pl={7} pr={2}>
      <ButtonGroup size="small" aria-label="small outlined button group">
        <Button onClick={() => props.switchFilter("All")}>All</Button>
        <Button onClick={() => props.switchFilter("Active")}>Active</Button>
        <Button onClick={() => props.switchFilter("Completed")}>
          Completed
        </Button>
        <Button onClick={() => props.switchFilter("Archived")}>Archived</Button>
      </ButtonGroup>
    </Box>
  );
}
