import React from "react";
import ArchiveCheckbox from "./Components/ArchiveCheckbox";
import CompleteCheckbox from "./Components/CompleteCheckbox";
import DeleteButton from "./Components/DeleteButton";
import Description from "./Components/Description";
import Checkbox from "@material-ui/core/Checkbox";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import ListItemText from "@material-ui/core/ListItemText";
import { makeStyles } from "@material-ui/core/styles";
import { grey } from "@material-ui/core/colors";
const useStyles = makeStyles((theme) => ({
  checkbox: {
    color: grey[700],
    "&:hover": { background: grey[300] },
    "&:checked": { background: grey[300] },
  },
}));
export default function RenderTodo(props) {
  // render() {
  const classes = useStyles();
  const todo = props.todo;
  return (
    <ListItem
      key={todo.id}
      role={undefined}
      dense
      button
      // onClick={handleToggle(value)}
    >
      <ListItemIcon>
        <Checkbox
          edge="start"
          iconStyle={{ fill: grey[700] }}
          inputStyle={{ color: grey[700] }}
          style={{ color: grey[700] }}
          className={classes.checkbox}
          checked={todo.status === "Completed"}
          // tabIndex={-1}
          disableRipple
          inputProps={{ "aria-labelledby": `${todo.id}` }}
          onChange={() => props.toggleTodo(todo.id)}
        />
        {/* <CompleteCheckbox todo={todo} toggleTodo={this.props.toggleTodo} /> */}
      </ListItemIcon>
      <ListItemText
        id={todo.id}
        primary={
          <Description
            todo={todo}
            handleDoubleClick={props.handleDoubleClick}
          />
        }
      >
        {/* <Description
            todo={todo}
            handleDoubleClick={this.props.handleDoubleClick}
          /> */}
      </ListItemText>
      <ListItemSecondaryAction>
        {/* <IconButton edge="end" aria-label="comments">
            <CommentIcon />
          </IconButton> */}
        {/* <ArchiveCheckbox todo={todo} archiveTodo={this.props.archiveTodo} /> */}
        {/* <DeleteButton todo={todo} deleteTodo={this.props.deleteTodo} /> */}
      </ListItemSecondaryAction>
    </ListItem>
    // <li id={todo.id}>
    //   <CompleteCheckbox todo={todo} toggleTodo={this.props.toggleTodo} />
    //   <Description
    //     todo={todo}
    //     handleDoubleClick={this.props.handleDoubleClick}
    //   />
    //   <ArchiveCheckbox todo={todo} archiveTodo={this.props.archiveTodo} />
    //   <DeleteButton todo={todo} deleteTodo={this.props.deleteTodo} />
    // </li>
  );
}
