import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
      width: "25ch",
    },
  },
}));

const ENTERKEY = 13;

export default class RenderEditingTodo extends React.Component {
  state = {
    description: this.props.todo.description || "",
  };
  handleSubmit = () => {
    this.props.saveTodoDescription(this.state.description);
  };
  handleBlur = () => {
    this.props.saveTodoDescription(this.state.description);
  };
  handleChange = (e) => {
    this.setState({ ...this.state, description: e.target.value });
  };

  render() {
    return (
      <li className="editTodoDescription">
        <form noValidate autoComplete="off" onSubmit={this.handleSubmit}>
          <TextField
            id="standard-basic"
            name="editedDescription"
            style={{ width: "60ch" }}
            value={this.state.description}
            onBlur={this.handleBlur}
            onChange={this.handleChange}
          />
        </form>
      </li>
      // <li id={this.props.todo.id} className="editTodoDescription">
      //   <textarea
      //     type="text"
      //     rows="1"
      //     placeholder=""
      //     autoFocus="true"
      //     value={this.state.description}
      //     onBlur={this.handleBlur}
      //     onChange={this.handleChange}
      //     onKeyDown={this.handleSubmit}
      //   />
      // </li>
    );
  }
}
